    /// Records a routed event handler for hot reload builds.
    /// </summary>
    /// <param name="builder">The target builder.</param>
    /// <param name="action">The action to run when the event fires.</param>
    /// <param name="routes">The routing strategies for the event.</param>
    /// <returns>The builder instance.</returns>
    public static ElementBuilder<T> OnRefreshRequestedHandler<T>(
        this ElementBuilder<T> builder,
        Action<T, Avalonia.Controls.RefreshRequestedEventArgs> action,
        Avalonia.Interactivity.RoutingStrategies routes = Avalonia.Interactivity.RoutingStrategies.Bubble) where T : Avalonia.Controls.RefreshContainer
    {
        return builder.WithEvent(new EventMutation(target =>
        {
            var typed = (Avalonia.Controls.RefreshContainer)target;
            void Handler(object? _, Avalonia.Controls.RefreshRequestedEventArgs args) => action((T)typed, args);
            typed.AddHandler(Avalonia.Controls.RefreshContainer.RefreshRequestedEvent, Handler, routes);
            return () => typed.RemoveHandler(Avalonia.Controls.RefreshContainer.RefreshRequestedEvent, Handler);
        }));
    }

    /// <summary>
    /// Records a routed event observable handler for hot reload builds.
    /// </summary>
    /// <param name="builder">The target builder.</param>
    /// <param name="handler">The handler receiving the observable.</param>
    /// <param name="routes">The routing strategies for the event.</param>
    /// <returns>The builder instance.</returns>
    public static ElementBuilder<T> OnRefreshRequested<T>(
        this ElementBuilder<T> builder,
        Action<T, IObservable<Avalonia.Controls.RefreshRequestedEventArgs>> handler,
        Avalonia.Interactivity.RoutingStrategies routes = Avalonia.Interactivity.RoutingStrategies.Bubble) where T : Avalonia.Controls.RefreshContainer
    {
        return builder.WithEvent(new EventMutation(target =>
        {
            var typed = (Avalonia.Controls.RefreshContainer)target;
            var observable = typed.GetObservable(Avalonia.Controls.RefreshContainer.RefreshRequestedEvent, routes);
            handler((T)typed, observable);
        }));
    }

#endif

    // Avalonia.Controls.RefreshContainer.RefreshRequested

    /// <summary>
    /// Adds a handler to the `RefreshRequested` event on the specified object.
    /// </summary>
    /// <param name="obj">The target object.</param>
    /// <param name="handler">The handler to be called when the event is raised.</param>
    /// <typeparam name="T">The type of the target object.</typeparam>
    /// <returns>The target object reference.</returns>
    public static T OnRefreshRequestedEvent<T>(this T obj, Action<T, IObservable<Avalonia.Controls.RefreshRequestedEventArgs>> handler) where T : Avalonia.Controls.RefreshContainer
    {
        var observable = Observable
            .FromEventPattern<System.EventHandler<Avalonia.Controls.RefreshRequestedEventArgs>, Avalonia.Controls.RefreshRequestedEventArgs>(
                h => obj.RefreshRequested += h, 
                h => obj.RefreshRequested -= h)
            .Select(x => x.EventArgs);
        handler(obj, observable);
        return obj;
    }

    /// <summary>
    /// Returns an observable for the `RefreshRequested` event on the specified object.
    /// </summary>
    /// <param name="obj">The target object.</param>
    /// <returns>An observable for the `RefreshRequested` event on the specified object.</returns>
    public static IObservable<Avalonia.Controls.RefreshRequestedEventArgs> ObserveOnRefreshRequestedEvent(this Avalonia.Controls.RefreshContainer obj)
    {
        return Observable
            .FromEventPattern<System.EventHandler<Avalonia.Controls.RefreshRequestedEventArgs>, Avalonia.Controls.RefreshRequestedEventArgs>(
                h => obj.RefreshRequested += h, 
                h => obj.RefreshRequested -= h)
            .Select(x => x.EventArgs);
    }
#if NXUI_HOTRELOAD

    /// <summary>
    /// Records a CLR event handler for hot reload builds.
    /// </summary>
    /// <param name="builder">The target builder.</param>
    /// <param name="handler">The handler receiving an observable.</param>
    /// <returns>The builder instance.</returns>
    public static ElementBuilder<T> OnRefreshRequestedEvent<T>(this ElementBuilder<T> builder, Action<T, IObservable<Avalonia.Controls.RefreshRequestedEventArgs>> handler) where T : Avalonia.Controls.RefreshContainer
    {
        return builder.WithEvent(new EventMutation(target =>
        {
            var typed = (Avalonia.Controls.RefreshContainer)target;
            var observable = Observable
                .FromEventPattern<System.EventHandler<Avalonia.Controls.RefreshRequestedEventArgs>, Avalonia.Controls.RefreshRequestedEventArgs>(
                    h => typed.RefreshRequested += h,
                    h => typed.RefreshRequested -= h)
                .Select(x => x.EventArgs);
            handler((T)typed, observable);
        }));
    }

#endif
}
