    public static ElementBuilder<T> OnClickEvent<T>(this ElementBuilder<T> builder, Action<T, IObservable<Avalonia.Interactivity.RoutedEventArgs>> handler) where T : Avalonia.Controls.SplitButton
    {
        return builder.WithEvent(new EventMutation(target =>
        {
            var typed = (Avalonia.Controls.SplitButton)target;
            var observable = Observable
                .FromEventPattern<System.EventHandler<Avalonia.Interactivity.RoutedEventArgs>, Avalonia.Interactivity.RoutedEventArgs>(
                    h => typed.Click += h,
                    h => typed.Click -= h)
                .Select(x => x.EventArgs);
            handler((T)typed, observable);
        }));
    }

}
